mapper.insert:
insert into fs_group (id, name, base_folder, mode
)
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{baseFolder,jdbcType=VARCHAR}, #{mode}
)

mapper.update:
update fs_group
set name = #{name,jdbcType=VARCHAR},
base_folder = #{baseFolder,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

update fs_group
set mode = #{mode}
where id = #{id}

mapper.select:
select id, name, base_folder, mode
from fs_group
where id = #{id,jdbcType=INTEGER}

select id, name, base_folder, mode
from fs_group


    id = #{p.id}


    and name like "%"#{p.name}"%"


    and mode = #{p.mode}
